try {
	def LIMIT = 11.7;
	def SIZE = 10;
	def mailrecipients = ["permasense-admin@tik.ee.ethz.ch"];
	if ( ! isdef('voltageMap')) { voltageMap = [:]; }
	if (POSITION != null && GENERATION_TIME > System.currentTimeMillis()-2592000000) {
		if (!voltageMap.containsKey(POSITION))
			voltageMap.put(POSITION, [0, []]);

		def ptr = voltageMap.get(POSITION)[0];
		def voltageList = voltageMap.get(POSITION)[1];

		org.apache.log4j.Logger.getLogger(gsn.processor.ScriptletProcessor.class).info("Position: " + POSITION + " voltageList.size()=" + voltageList.size());
		if (voltageList.size() == SIZE) {
			def averageBefore = voltageList.sum() / SIZE;
			voltageList[ptr] = SDIVOLTAGE.toDouble();

			if ((voltageList.sum() / SIZE) <= LIMIT && averageBefore > LIMIT) {
				// Notify by email
				def emailTitle = "[PermaSense-GSN-Public] - VOLTAGE WARNING - " + vsname.split("_",2)[0].toUpperCase() + " POS " + POSITION + " - Battery voltage reaches critical state";
				def emailContent = "VOLTAGE WARNING - " + vsname.split("_",2)[0].toUpperCase() + " POS " + POSITION + ":\n\n Battery voltage dropped below " + LIMIT + "V.";

				sendEmail(mailrecipients, emailTitle, emailContent);
			}
		}
		else { voltageList[ptr] = SDIVOLTAGE.toDouble(); }

		ptr = (ptr+1)%SIZE;
		voltageMap.put(POSITION, [ptr, voltageList]);
	}

	if ( ! isdef('positionMap')) { positionMap = [:]; }
	if (POSITION != null && GENERATION_TIME > System.currentTimeMillis()-2592000000) { positionMap.put(POSITION, System.currentTimeMillis()); }
} catch(Throwable e) {
	org.apache.log4j.Logger.getLogger(gsn.processor.ScriptletProcessor.class).error(e);
}


                	try {
                		// default alarm time after which an email is sent if no messages have been received
                    	def timeDifferenceOffsetDefault = 86400000;
                		// a list of alarm time per position
                    	def timeDifferenceOffsetPositionMap = [:];
                    	// the mail recipients
                    	def mailrecipients = ["permasense-admin@tik.ee.ethz.ch"];

                    	if ( isdef('positionMap')) {
                    		def rmlist = [];
                    		for (entry in positionMap) {
                    	    	def timeDifference = System.currentTimeMillis() - entry.value;
                    	    	def timeDifferenceOffset = timeDifferenceOffsetDefault;
                    	    	if (timeDifferenceOffsetPositionMap.containsKey(entry.key)) {
                    	    		timeDifferenceOffset = timeDifferenceOffsetPositionMap.get(entry.key);
                    	    	}
                    	    	if (timeDifference > timeDifferenceOffset) {
                    	    		// Notify by email
                    	    		def emailTitle = "[PermaSense-GSN-Public] - WARNING - " + vsname.split("_",2)[0].toUpperCase() + " POS " + entry.key + " - No more " + vsname + " messages received";
                    	    		def emailContent = "WARNING - " + vsname.split("_",2)[0].toUpperCase() + " POS " + entry.key + ":\n\n No " + vsname + " messages received in the last " + (timeDifference/60000.0) + " minutes.";

                       		 		sendEmail(mailrecipients, emailTitle, emailContent);
                        			rmlist.add(entry.key)
                        		}
                      		}
                        	positionMap.keySet().removeAll( rmlist as Set );
                    	}
					} catch(Throwable e) {
						org.apache.log4j.Logger.getLogger(gsn.processor.ScriptletProcessor.class).error(e);
					}
