init-params.param:

try {
  def spl = new String(RAW_PACKET, "UTF-8").split(",",-1);

  if (spl.length == 7) {

    POSITION = 301;
    DEVICE_ID = 301;
    ANDROID_ID = spl[1].toUpperCase();

    if (spl[2].isNumber())
      GENERATION_TIME = spl[2].toLong();
    if (spl[3].isNumber())
      LATITUDE = ((spl[3].toDouble()-spl[3].toDouble().intValue())*60.0) + spl[3].toDouble().intValue()*100.0;
    if (spl[4].isNumber())
      LONGITUDE = ((spl[4].toDouble()-spl[4].toDouble().intValue())*60.0) + spl[4].toDouble().intValue()*100.0;
    if (spl[5].isNumber())
      spl[5].toInteger();
    if (spl[6].isNumber())
      spl[6].toInteger();
  }
} catch(Throwable e) {
    org.apache.log4j.Logger.getLogger(gsn.processor.ScriptletProcessor.class).error(e);
}
